import React from 'react';
import './news.css';
import { useState, useEffect } from 'react';


const News = () => {
    const [news, setNews] = useState([]);
    // const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        fetch('http://127.0.0.1:5000/api/file')
            .then(response => response.json())
            .then(data => setNews(data))
            .catch(error => console.error('Error fetching data:', error));
    }, []);
    return (
        <div className="App">
            <header className="header">
                <p className='title'>{news.title}</p>
                <div className="author-info">
                    <p className="author">Tên tác giả</p> <p> - </p>
                    <p className="publish-date">{news.Time}</p>

                </div>
                <p className='summary' >{news.summary}</p>
            </header>
            <div className="content">
                <div className='img'>
                    <img src='C:\Users\Thanh\Desktop\crawldata\selenium\.venv\crawl\frontend/public/images/00ukraine-weapons-gqkz-superjumbo-crop-1692158782298.jpeg' alt="Hình ảnh minh họa" />
                    <p className='chuthich'>Chú thích ảnh</p>
                </div>
                <p>{news.content}</p>

            </div>
        </div>
    );
}

export default News;
