import React, { useState, useEffect } from 'react';

function Show() {
  const [fileList, setFileList] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const filesPerPage = 12;

  useEffect(() => {
    fetch('http://127.0.0.1:5000/api/files')
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch files');
        }
        return response.json();
      })
      .then(data => {
        setFileList(data.files);
      })
      .catch(error => {
        console.error(error.message);
      });
  }, []);

  const handleClickNext = () => {
    setCurrentPage(prevPage => prevPage + 1);
  };

  const indexOfLastFile = currentPage * filesPerPage;
  const indexOfFirstFile = indexOfLastFile - filesPerPage;
  const currentFiles = fileList.slice(indexOfFirstFile, indexOfLastFile);

  return (
    <div className="container">
      {/* <h1 style={{ marginTop: 0, marginBottom: '1em' }}>File List</h1> */}
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <table>
          <thead>
            <tr>
              <th>Number</th>
              <th>File Name</th>
            </tr>
          </thead>
          <tbody>
            {currentFiles.map((file, index) => (
              <tr key={index}>
                <td>{indexOfFirstFile + index + 1}</td>
                <td>{file}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div style={{ display: 'flex', justifyContent: 'center', marginTop: '1em' }}>
        {fileList.length > indexOfLastFile && (
          <button onClick={handleClickNext}>Next</button>
        )}
      </div>
    </div>
  );
}

export default Show;
