import React, { useState, useEffect } from 'react';
import './CrawlDuLieu.css';
import { fetchWebsites } from '../../services/api'; // Import hàm fetchWebsites từ api.js
import Show from '../Show/Show.js';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import axios from 'axios';

const CrawlDuLieu = () => {
  // đặt độ rộng cho từng cột trong table
  const cellWidth = {
    column1: '10%', // Độ rộng cho cột 1
    column2: '20%',
    column3: '50%',
    column4: '20%'
  };

  const [websites, setWebsites] = useState([]);
  const [websiteName, setWebsiteName] = useState('');
  const [websiteLink, setWebsiteLink] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [showAlert, setShowAlert] = useState(false);


  const handleSubmit = (e) => {
    e.preventDefault();
    // Xử lý logic để thêm bản ghi mới với websiteName và websiteLink
    console.log('Tên website:', websiteName);
    console.log('Link:', websiteLink);
    // Đặt state về giá trị ban đầu sau khi thêm bản ghi
    setWebsiteName('');
    setWebsiteLink('');
  };




  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchButtonClick = () => {
    // Thực hiện tìm kiếm dựa trên giá trị của searchTerm
    setSearchTerm('');
    setShowAlert(true);

  };
  const handleCrawlClick = async (url) => {
    try {
      // Gọi API '/api/run-dantri' khi nhấn nút "Crawl"
      const linkCrawlElement = document.getElementById('linkcrawl');
      if (linkCrawlElement) {
        const url = linkCrawlElement.textContent; // hoặc innerText
        const response = await axios.post('http://127.0.0.1:5000/crawl', {
          url: url
        })}
        console.log(response.data.message); // In ra thông báo từ API (tuỳ theo thiết kế API của bạn)
      } catch (error) {
        alert.error('Lỗi khi thực hiện crawl:', error);
      }
    };

    //api
    const fetchData = async () => { // Định nghĩa hàm fetchData
      try {
        const data = await fetchWebsites();
        setWebsites(data);
      } catch (error) {
        // Xử lý lỗi nếu cần
      }
    };


    useEffect(() => {
      fetchData(); // Gọi hàm fetchData khi component mount
    }, []);
    const [url, setUrl] = useState('');
    const [loading, setLoading] = useState(false);

    const handleCrawl = async () => {
      try {
        setLoading(true);
        const response = await fetch('http://127.0.0.1:5000/crawl', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ url }),
        });
        const data = await response.json();
        console.log(data.message);
      } catch (error) {
        console.error(error);
      } finally {
        setLoading(false);
      }
    };


    return (
      <div>
        <div className="search-container">
          <input
            type="text"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            placeholder="Enter URL"
            className="search-input" />
          <button className="crawl_search" onClick={handleCrawl} disabled={loading}>
            {loading ? 'Crawl...' : 'Crawl'}
          </button>
        </div>
        {/* <div>
        <h1>Danh sách website </h1>
        <br></br>
      </div> */}

        <table>
          <thead>
            <tr>
              <th style={{ width: cellWidth.column1 }}>STT</th>
              <th style={{ width: cellWidth.column2 }}>Tên website</th>
              <th style={{ width: cellWidth.column3 }}>Link</th>
              <th style={{ width: cellWidth.column4 }}>Hành động</th>
            </tr>
          </thead>

          <tbody>
            {/*  
            {websites.map((website, index) => (
              <tr key={website.id}>
                <td style={{ width: cellWidth.column1 }}>{index + 1}</td>
                <td style={{ width: cellWidth.column2 }}>{website.name}</td>
                <td style={{ width: cellWidth.column3 }}>{website.link}</td>
                <td style={{ width: cellWidth.column4 }}>
                  <button className='CrawlButton'>Crawl</button>
                  <Link to="/show-txt">
                    <button className='FileButton'>Show</button>
                  </Link>
                </td>
              </tr>
            ))}
             */}
            <tr >
              <td style={{ width: cellWidth.column1 }}>1</td>
              <td style={{ width: cellWidth.column2 }}>dantri</td>
              <td id="linkcrawl" style={{ width: cellWidth.column3 }}>www.dantri.com</td>
              <td style={{ width: cellWidth.column4 }}>
                <button className='CrawlButton' onClick={handleCrawlClick} >Crawl</button>
                <Link to="/show-txt">
                  <button className='FileButton'>Show</button>
                </Link>
              </td>
            </tr>
          </tbody>



        </table>

      </div>
    );
  };

  export default CrawlDuLieu;
