import React from 'react';
import './news.css';
import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const News = () => {
    const { filename } = useParams();
    const [newsData, setNewsData] = useState({});

    useEffect(() => {
        fetch(`http://127.0.0.1:5000/api/file/${encodeURIComponent(filename)}`)
            .then(response => response.json())
            .then(data => setNewsData(data))
            .catch(error => console.error('Error fetching data:', error));
    }, [filename]);
    return (
        <div className="App">
            <header className="header">
                <p className='title'>Trang Báo Đơn Giản</p>
                <div className="author-info">
                    <p className="author">Tên tác giả</p> <p> - </p>
                    <p className="publish-date">Thời gian</p>
                </div>
            </header>
            <div className="content-new">
                <div className='img'>
                    <img src={sky} alt="Hình ảnh minh họa" className='imgNew' />
                    <p className='chuthich'>Chú thích ảnh</p>
                </div>
                <p></p>
                <p>Tất cả thay đổi cần thiết đã được đưa vào hệ thống Pantsir-S, giúp tăng hiệu quả chống lại các loại đạn "khó đối phó" và tên lửa tầm xa, bao gồm cả Storm Shadow - loại tên lửa hành trình tầm xa khó phát hiện", đại diện công ty nói với TASS.

                    "Kết quả trong thực tế tác chiến đã chứng minh tính đúng đắn của các quyết định thiết kế trước đây", người đại diện nói.

                    Công ty này lưu ý rằng từ rất lâu trước khi có "chiến dịch quân sự đặc biệt", hệ thống Pantsir-S đã được thiết kế như hệ thống phòng thủ đa dụng trước mọi loại vũ khí, bao gồm chống lại tên lửa hành trình.

                    Trước đó, Rostec nói với TASS rằng hiệu quả của các hệ thống Pantsir đã được nâng cấp trước tên lửa HIMARS là 100%.

                    Hệ thống phòng thủ Pantsir của Nga là hệ thống phòng không cơ động có phạm vi hoạt động 20 km, đã được đưa vào biên chế quân đội Nga từ năm 2003 với nhiều phiên bản khác nhau.

                    Nó thường được dùng để bảo vệ các khu vực mục tiêu trọng yếu, các điểm dân cư và quân sự, đội hình đơn vị chiến đấu đang cơ động lên tới cỡ trung đoàn hoặc các tổ hợp phòng không khác như S-300/S-400.

                    Theo Bộ Quốc phòng Nga, mỗi tổ thao tác Pantsir trong "chiến dịch quân sự đặc biệt" thường phát hiện và tiêu diệt được vài mục tiêu mỗi ca.

                    Các hệ thống Pantsir cho phép người điều khiển phát hiện không chỉ tên lửa hành trình, tên lửa đạn đạo, trực thăng, máy bay cánh cố định… mà còn cả các UAV thương mại kích cỡ 30x30cm phổ thông vốn thường được Ukraine sử dụng để trinh sát và tấn công cảm tử.

                </p>
            </div>
        </div>
    );
}

export default News;
