import React from 'react';
import './news.css';
import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const News = () => {
    const { filename } = useParams();
    const [newsData, setNewsData] = useState({});

    useEffect(() => {
        fetch(`http://127.0.0.1:5000/api/file/${encodeURIComponent(fileName)}`)
            .then(response => response.json())
            .then(data => setNewsData(data))
            .catch(error => console.error('Error fetching data:', error));
    }, [fileName]);
    return (
        <div className="App">
            <header className="header">
                <p className='title'>{newsData.title}</p>
                <div className="author-info">
                    <p className="author">Tên tác giả</p> <p> - </p>
                    <p className="publish-date">{newsData.Time}</p>

                </div>
                <p className='summary' >{newsData.summary}</p>
            </header>
            <div className="content">
                <div className='img'>
                    <img src={newsData.img} alt="Hình ảnh minh họa" />
                    <p className='chuthich'>Chú thích ảnh</p>
                </div>
                <p>{newsData.content}</p>

            </div>
        </div>
    );
}

export default News;
