import React, { useState, useEffect } from 'react';
import './CrawlDuLieu.css';
import { fetchWebsites } from '../../services/api'; // Import hàm fetchWebsites từ api.js
import Show from '../Show/Show.js';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import queryString from 'query-string'; // Import thư viện query-string


const CrawlDuLieu = () => {
  // đặt độ rộng cho từng cột trong table
  const cellWidth = {
    column1: '10%',
    column2: '10%',
    column3: '20%',
    column4: '40%',
    column5: '20%'
  };

  const [websites, setWebsites] = useState([]);
  const [websiteName, setWebsiteName] = useState('');
  const [websiteLink, setWebsiteLink] = useState('');
  const [NewsPage, setNewsPage] = useState('');

  const [searchTerm, setSearchTerm] = useState('');
  const [showAlert, setShowAlert] = useState(false);
  const [isLoading, setIsLoading] = useState(false); // quản lý trạng thái của modal
  // Lấy thông tin về location từ React Router
  const location = useLocation();
  // Kiểm tra xem tham số active có trong URL không
  const isActive = location.search.includes('active=namenews');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Xử lý logic để thêm bản ghi mới với websiteName và websiteLink
    console.log('Tên website:', websiteName);
    console.log('Link:', websiteLink);
    // Đặt state về giá trị ban đầu sau khi thêm bản ghi
    setWebsiteName('');
    setWebsiteLink('');
  };

  // const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const namenews = searchParams.get('namenews') || '';
  const [newsData, setNewsData] = useState([]);
  useEffect(() => {
    fetch(`http://127.0.0.1:5000/api/child-link`)
      .then(response => response.json())
      .then(data => setNewsData(data))
      .catch(error => console.error('Error fetching data:', error));
  }, []);




  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchButtonClick = () => {
    // Thực hiện tìm kiếm dựa trên giá trị của searchTerm
    setSearchTerm('');
    setShowAlert(true);

  };
  const [isCrawling, setIsCrawling] = useState(false);
  const handleCrawlClick = (id) => {
    setIsCrawling(true);
    if (window.confirm("Bạn có chắc chắn muốn crawl trang báo này?")) {
      fetch(`http://127.0.0.1:5000/api/run_dantri_script/${id}`, {
        method: 'GET'
      })
        .then(response => response.json())
        .then(data => {
          console.log(data);
          setIsCrawling(false);
          // Sau khi xóa thành công, gọi lại API để cập nhật danh sách

        })
        .catch(error => console.error('Error deleting article:', error));
    }
    else {
      setIsCrawling(false);
    }
  };

  //api
  const fetchData = async () => { // Định nghĩa hàm fetchData
    try {
      const data = await fetchWebsites();
      setWebsites(data);
    } catch (error) {
      // Xử lý lỗi nếu cần
    }
  };


  useEffect(() => {
    fetchData(); // Gọi hàm fetchData khi component mount
  }, []);
  const [url, setUrl] = useState('');
  const [loading, setLoading] = useState(false);

  const handleCrawl = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://127.0.0.1:5000/crawl', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url }),
      });
      const data = await response.json();
      console.log(data.message);
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };




  return (
    <div>
      {/* <div className="search-container">
        <input
          type="text"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          placeholder="Enter URL"
          className="search-input" />
        <button className="them" onClick={handleCrawl} > Thêm</button>
        <button className="searchbutton" onClick={handleSearchButtonClick}>Search</button>
      </div> */}
      <div className="container">
        <div className="search-container">
          <input type="text" placeholder="Nhập từ khóa cần tìm..." value={searchTerm} onChange={handleSearch} className="search-input" />
          <button className="search-button" onClick={handleSearchButtonClick}>
            Search
          </button>
        </div>
        <div className="container">
          <div class="form-container">
            <form onSubmit={handleSubmit}>

              <div>
                <label htmlFor="websiteName" className="label1"> Tên website:</label>
                <input type="text" id="websiteName"
                  value={websiteName}
                  onChange={(e) => setWebsiteName(e.target.value)}
                  required
                />
                <div />
                <div>
                  <label htmlFor="websiteLink" className="label2">Link:</label>
                  <input type="text" id="websiteLink"
                    value={websiteLink}
                    onChange={(e) => setWebsiteLink(e.target.value)}
                    required
                  />
                </div>
                <div>
                  <label htmlFor="NewsPage" className="label1">NewsPage:</label>
                  <input type="text" id="NewsPage"
                    value={NewsPage}
                    onChange={(e) => setNewsPage(e.target.value)}
                    required
                  />
                </div>

                <div className="button-container">

                  <button className='ThemButton' >Thêm mới</button>
                </div>
              </div>
            </form>
          </div>
        </div>

      </div>

      {/* <div>
        <h1>Danh sách website </h1>
        <br></br>
      </div> */}
      <div className='crawldl' >
        <table>
          <thead>
            <tr>
              <th style={{ width: cellWidth.column1 }}>STT</th>
              <th style={{ width: cellWidth.column2 }}>Name</th>
              <th style={{ width: cellWidth.column3 }}>Tên website</th>
              <th style={{ width: cellWidth.column4 }}>Link</th>
              <th style={{ width: cellWidth.column5 }}>Hành động</th>
            </tr>
          </thead>

          <tbody>
            {newsData.map((childname, index) => (
              <tr >
                <td style={{ width: cellWidth.column1 }}>{index + 1}</td>
                <td style={{ width: cellWidth.column2 }}>{childname.NameNews}</td>
                <td style={{ width: cellWidth.column3 }}>{childname.NameItem}</td>
                <td style={{ width: cellWidth.column4 }}>{childname.Link}</td>
                <td style={{ width: cellWidth.column5 }}>
                  <button className='CrawlButton' onClick={() => handleCrawlClick(childname.Id)} disabled={isCrawling}>{isCrawling ? "Đang Crawl..." : "Crawl"}</button>
                  <Link to="/show-txt">
                    <button className='FileButton'>Show</button>
                  </Link>
                </td>
              </tr>
            ))}


          </tbody>




        </table>
      </div>
      <div>

      </div>
      {isLoading && (
        <div className="modal">
          <div className="modal-content">
            <p>Loading...</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default CrawlDuLieu;
