// QuanLyWebsite.js
import React, { useState, useEffect } from 'react';
import { fetchWebsites, addWebsite, deleteWebsite } from '../../services/api';
import '../SearchBar/SearchBar.js';
import './QuanLyWeb.css';
import search from '../../assets/image/search.png';





const QuanLyWebsite = () => {
  // đặt độ rộng cho từng cột trong table
  const cellWidth = {
    column1: '10%', // Độ rộng cho cột 1
    column2: '20%',
    column3: '50%',
    column4: '20%'
  };

  const [websiteName, setWebsiteName] = useState('');
  const [websiteLink, setWebsiteLink] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [showAlert, setShowAlert] = useState(false);
  const [websites, setWebsites] = useState([]);
  // lay danh sach trang bao 
  const [newspage, setNewspage] = useState([]);


  useEffect(() => {
    fetch('http://127.0.0.1:5000/api/newspaper_page')
      .then(response => response.json())
      .then(data => setNewspage(data))
      .catch(error => console.error('Error fetching data:', error));
  }, []);
  // delete 
  const handleDeleteButtonClick = (id) => {
    if (window.confirm("Bạn có chắc chắn muốn xóa bài báo này?")) {
      fetch(`http://127.0.0.1:5000/api/newspaper_page/${id}`, {
        method: 'DELETE'
      })
        .then(response => response.json())
        .then(data => {
          console.log(data);
          // Sau khi xóa thành công, gọi lại API để cập nhật danh sách
          fetch('http://127.0.0.1:5000/api/newspaper_page') // Gọi lại API để lấy danh sách mới
            .then(response => response.json())
            .then(updatedData => setNewspage(updatedData)) // Cập nhật newspage với danh sách mới
            .catch(error => console.error('Error fetching updated data:', error));
        })
        .catch(error => console.error('Error deleting article:', error));
    }
  };
  // add new newspage
  const handleAddButtonClick = async (website) => {
    try {
      const response = await fetch('http://127.0.0.1:5000/api/newspaper_page', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
         
        })
      });

      const data = await response.json();
      console.log(data);

      // Gọi lại API để cập nhật danh sách mới và cập nhật state
      fetch('http://127.0.0.1:5000/api/newspaper_page') // Gọi lại API để lấy danh sách mới
        .then(response => response.json())
        .then(updatedData => setNewspage(updatedData)) // Cập nhật newspage với danh sách mới
        .catch(error => console.error('Error fetching updated data:', error));
      setWebsiteName('');
      setWebsiteLink('');
    } catch (error) {
      console.error('Error adding article:', error);
    }
  };

  // edit
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await addWebsite(websiteName, websiteLink);
      console.log('Thêm mới thành công:', response);
      fetchData();// Gọi hàm fetchData sau khi thêm thành công
      // Đặt state về giá trị ban đầu sau khi thêm bản ghi
      setWebsiteName('');
      setWebsiteLink('');
    } catch (error) {
      console.error('Lỗi khi thêm mới:', error);
    }


  };

  const handleEditButtonClick = async () => {
    try {
      const response = await fetch('http://127.0.0.1:5000/api/newspaper_page', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          NameNews: websiteName,
          Link: websiteLink
        })
      });

      const data = await response.json();
      console.log(data);

      // Gọi lại API để cập nhật danh sách mới và cập nhật state
      fetch('http://127.0.0.1:5000/api/newspaper_page') // Gọi lại API để lấy danh sách mới
        .then(response => response.json())
        .then(updatedData => setNewspage(updatedData)) // Cập nhật newspage với danh sách mới
        .catch(error => console.error('Error fetching updated data:', error));
      setWebsiteName('');
      setWebsiteLink('');
    } catch (error) {
      console.error('Error adding article:', error);
    }
  };
  const handleNewspageUpdate = (e, id, field) => {
    const newValue = e.target.textContent.trim();
    const updatedNewspage = newspage.map(websize =>
      websize.Id === id ? { ...websize, [field]: newValue } : websize
    );
    setNewspage(updatedNewspage);
  };


  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchButtonClick = () => {
    // Thực hiện tìm kiếm dựa trên giá trị của searchTerm
    setSearchTerm('');
    setShowAlert(true);

  };





  //api
  const fetchData = async () => { // Định nghĩa hàm fetchData
    try {
      const data = await fetchWebsites();
      setWebsites(data);
    } catch (error) {
      // Xử lý lỗi nếu cần
    }
  };


  useEffect(() => {
    fetchData(); // Gọi hàm fetchData khi component mount
  }, []);



  return (
    <div>
      <div className="container">
        <div className="search-container">
          <input type="text" placeholder="Nhập từ khóa cần tìm..." value={searchTerm} onChange={handleSearch} className="search-input" />
          <button className="search-button" onClick={handleSearchButtonClick}>
            <img src={search}></img>
          </button>
        </div>
        <div className="container">
          <div class="form-container">
            <form onSubmit={handleSubmit}>
              <div>
                <label htmlFor="websiteName" className="label1"> Tên website:</label>
                <input type="text" id="websiteName"
                  value={websiteName}
                  onChange={(e) => setWebsiteName(e.target.value)}
                  required
                />
                <div />
                <div>
                  <label htmlFor="websiteLink" className="label2">Link:</label>
                  <input type="text" id="websiteLink"
                    value={websiteLink}
                    onChange={(e) => setWebsiteLink(e.target.value)}
                    required
                  />
                </div>
                <div className="button-container">

                  <button className='ThemButton' onClick={handleAddButtonClick}>Thêm mới</button>
                  <button type="submit"  >Xác nhận</button>
                </div>
              </div>
            </form>
          </div>
        </div>

      </div>
      <div>
        <table>
          <thead>
            <tr>
              <th style={{ width: cellWidth.column1 }}>STT</th>
              <th style={{ width: cellWidth.column2 }}>Tên website</th>
              <th style={{ width: cellWidth.column3 }}>Link</th>
              <th style={{ width: cellWidth.column4 }}>Hành động</th>
            </tr>
          </thead>

          <tbody>
            {newspage.map((website, index) => (
              <tr key={index}>
                <td style={{ width: cellWidth.column1 }}>{index + 1}</td>
                <td style={{ width: cellWidth.column2 }} contentEditable={true} onBlur={(e) => handleNewspageUpdate(e, website.Id, 'NameNews', website.NameNews)}>{website.NameNews}</td>
                <td style={{ width: cellWidth.column3 }} contentEditable={true} onBlur={(e) => handleNewspageUpdate(e, website.Id, 'Link', website.NameNews)}>{website.Link}</td>
                <td style={{ width: cellWidth.column4 }}>
                  <button className='SuaButton' onClick={() => handleEditButtonClick(website)}>Sửa</button>
                  <button className='XoaButton' onClick={() => handleDeleteButtonClick(website.Id)}  >Xóa</button>
                </td>
              </tr>
            ))}
          </tbody>

        </table>
      </div>
    </div>
  );
};


export default QuanLyWebsite;
